package main

import (
	"strconv"
)

const wtf = ""

func fiveDigitProduct(start int) int {
	a, _ := strconv.Atoi(wtf[start : start+1])
	b, _ := strconv.Atoi(wtf[start+1 : start+2])
	c, _ := strconv.Atoi(wtf[start+2 : start+3])
	d, _ := strconv.Atoi(wtf[start+3 : start+4])
	e, _ := strconv.Atoi(wtf[start+4 : start+5])
	return a * b * c * d * e
}

func largestProductInSeries() int {
	max := 0
    n := len(wtf) - 4
    for i := 0; i < n; i++ {
        product := fiveDigitProduct(i)
        if product > max {
        	max = product
        }
    }
	return max
}
